import React, { Component } from 'react';
import './App.css';
import Cardlist from './Cardlist';
import { robots } from "./Robots";
import SearchBox from './SearchBox'




class App extends Component {
  constructor(){
    super()
    this.state = {
      robots: [],
      searchfield: ''
    }
  }

  componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response=> response.json())
      .then(users =>{
        this.setState({ robots: users})
      })

  }

  onSearchBarChange = (event) => {
    console.log('hello world searched ' + event.target.value)
    this.setState({ searchfield: event.target.value })
  }

  render(){
    const fiteredRobots = this.state.robots.filter(robot => { 
      return robot.name.toLowerCase().includes(this.state.searchfield.toLowerCase())
    })

    return (
      <div className='tc'>
        <h1 className="f3">RoboFriends</h1> 
        <SearchBox searchChange={this.onSearchBarChange} />
        <Cardlist robots={ fiteredRobots } />
      </div>
    );
  }

}

export default App;
